"use strict";document.addEventListener("DOMContentLoaded",()=>{{var e=".header";const s=document.querySelector(e);window.onscroll=()=>{0<window.scrollY?(s.classList.add("header--scroll"),s.classList.remove("header-special"),s.classList.add("fade-in")):(s.classList.remove("header--scroll"),s.classList.add("header-special"),s.classList.remove("fade-in"))}}});